document.addEventListener("DOMContentLoaded",(function(){var e=document.getElementById("external-events-list");new FullCalendar.Draggable(e,{itemSelector:".fc-event",eventData:function(e){return{title:e.innerText.trim()}}});var t=document.getElementById("draggableCalendar");new FullCalendar.Calendar(t,{headerToolbar:{left:"prev,next today",center:"title",right:"dayGridMonth,timeGridWeek,timeGridDay,listWeek"},editable:!0,droppable:!0,drop:function(e){document.getElementById("drop-remove").checked&&e.draggedEl.parentNode.removeChild(e.draggedEl)}}).render()}));